import React from 'react';
import { useCart } from 'react-use-cart';

function AddToCart() {
  const { isEmpty, totalUniqueItems, items, cartTotal, updateItemQuantity, removeItem, emptyCart } = useCart();

  const handleUpdateQuantity = (item, quantity) => {
    updateItemQuantity(item.id, quantity);
  };

  const handleRemoveItem = (item) => {
    removeItem(item.id);
  };

  if (isEmpty) {
    return <h1 className='text-center'>Your Cart is Empty</h1>;
  }

  return (
    <section className='py-4 container'>
      <div className='row justify-content-center'>
        <div className='col-12'>
          <h3>Cart ({totalUniqueItems}) total Items</h3>
          <table className='table table-light table-hover m-0'>
            <tbody>
              {items.map((item) => (
                <tr key={item.id}>
                  <td>
                    <img src={item.image} alt={item.name} style={{ width: '100px', height: 'auto' }} />
                  </td>
                  <td>{item.name}</td>
                  <td>${item.price}</td>
                  <td>
                    <button className='btn btn-info btn-sm' onClick={() => handleUpdateQuantity(item, item.quantity - 1)}>
                      -
                    </button>
                    {item.quantity}
                    <button className='btn btn-info btn-sm' onClick={() => handleUpdateQuantity(item, item.quantity + 1)}>
                      +
                    </button>
                  </td>
                  <td>
                    <button className='btn btn-danger btn-sm' onClick={() => handleRemoveItem(item)}>
                      Remove
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <div className='mt-3'>
            <button className='btn btn-danger btn-sm' onClick={() => emptyCart()}>
              Clear Cart
            </button>
          </div>
          <div className='mt-3'>
            <h4>Total: ${cartTotal.toFixed(2)}</h4>
          </div>
        </div>
      </div>
    </section>
  );
}

export default AddToCart;
