import React, { useState, useEffect } from 'react';

function PopularDishes() {
  const [dishes, setDishes] = useState([]);

  // Simulate fetching data from the JSON file
  
  useEffect(() => {
    // Fetch the JSON file from its public URL
    fetch('/list.json') // Adjust the path as per your project setup
      .then(response => response.json())
      .then(data => setDishes(data))
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  return (
   
      <div className="container mx-auto">
        <h2 className="text-3xl font-bold text-center mb-8">Popular Dishes</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {dishes.map((dish) => (
            <div key={dish.id} className="bg-white p-4 shadow-lg">
              <img src={dish.image} alt={dish.name} className="w-72 h-48 object-cover mb-4" />
              <h3 className="text-xl font-bold">{dish.name}</h3>
              <p className="text-gray-700">{dish.description}</p>
              <p className="text-gray-900 font-bold">${dish.price}</p>
            </div>
          ))}
        </div>
      </div>
 
  );
}

export default PopularDishes;
