import React from 'react';
import { useCart } from 'react-use-cart';

function AddToCart() {
  const { isEmpty, totalUniqueItems, items, cartTotal, updateItemQuantity, removeItem, emptyCart } = useCart();

  const handleUpdateQuantity = (item, quantity) => {
    updateItemQuantity(item.id, quantity);
  };

  const handleRemoveItem = (item) => {
    removeItem(item.id);
  };

  if (isEmpty) {
    return <h1 className='text-center'>Your Cart is Empty</h1>;
  }

  return (
    <section className='py-4 container mx-auto'>
      <div className='max-w-2xl mx-auto'>
        <h3 className='text-2xl font-semibold text-center mb-4'>Cart {totalUniqueItems} total Items</h3>
        <div className='overflow-x-auto'>
          <table className='table-auto w-full border-collapse border border-gray-200'>
            <thead>
              <tr className='bg-gray-100'>
                <th className='border border-gray-300 px-4 py-2'>Image</th>
                <th className='border border-gray-300 px-4 py-2'>Name</th>
                <th className='border border-gray-300 px-4 py-2'>Price</th>
                <th className='border border-gray-300 px-4 py-2'>Quantity</th>
                <th className='border border-gray-300 px-4 py-2'>Actions</th>
              </tr>
            </thead>
            <tbody>
              {items.map((item) => (
                <tr key={item.id} className='text-center'>
                  <td className='border border-gray-300 px-4 py-2'>
                    <img src={item.image} alt={item.name} className='w-24 h-auto' />
                  </td>
                  <td className='border border-gray-300 px-4 py-2'>{item.name}</td>
                  <td className='border border-gray-300 px-4 py-2'>${item.price.toFixed(2)}</td>
                  <td className='border border-gray-300 px-4 py-2'>
                    <button
                      className='bg-gray-200 hover:bg-gray-300 px-2 py-1 rounded'
                      onClick={() => handleUpdateQuantity(item, item.quantity - 1)}
                    >
                      -
                    </button>
                    <span className='px-2'>{item.quantity}</span>
                    <button
                      className='bg-gray-200 hover:bg-gray-300 px-2 py-1 rounded'
                      onClick={() => handleUpdateQuantity(item, item.quantity + 1)}
                    >
                      +
                    </button>
                  </td>
                  <td className='border border-gray-300 px-4 py-2'>
                    <button
                      className='bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded'
                      onClick={() => handleRemoveItem(item)}
                    >
                      Remove
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <div className='text-center mt-4'>
          <button
            className='bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded'
            onClick={() => emptyCart()}
          >
            Clear Cart
          </button>
          <h4 className='mt-3 text-xl'>Total: ${cartTotal.toFixed(2)}</h4>
        </div>
      </div>
    </section>
  );
}

export default AddToCart;
